    marginTop: 8,
  },
  categoryChip: {
    margin: 4,
  },
  noCategories: {
    color: '#666',
    fontStyle: 'italic',
  },
  accountButton: {
    marginVertical: 8,
  },
  footer: {
    padding: 24,
    alignItems: 'center',
  },
  versionText: {
    color: '#666',
    fontSize: 12,
  },
  input: {
    marginBottom: 16,
    backgroundColor: 'white',
  },
  preferencesLabel: {
    fontSize: 16,
    marginTop: 16,
    marginBottom: 8,
  },
  radioOption: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 4,
  },
  radioLabel: {
    fontSize: 16,
    marginLeft: 8,
  },
  categoriesSelector: {
    flexDirection: 'row',
