import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  Image,
  Dimensions
} from 'react-native';
import { Button, Chip, ProgressBar } from 'react-native-paper';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../../App';

type OnboardingScreenNavigationProp = StackNavigationProp<RootStackParamList, 'Onboarding'>;

interface OnboardingScreenProps {
  navigation: OnboardingScreenNavigationProp;
}

const { width } = Dimensions.get('window');

// Define the reading frequency options
const readingFrequencyOptions = [
  { id: 1, label: 'Daily', value: 'daily' },
  { id: 2, label: 'Few times a week', value: 'weekly' },
  { id: 3, label: 'Occasionally', value: 'occasionally' },
  { id: 4, label: 'Rarely', value: 'rarely' },
];

// Define the age group options
const ageGroupOptions = [
  { id: 1, label: 'Under 18', value: 'under_18' },
  { id: 2, label: '18-24', value: '18_24' },
  { id: 3, label: '25-34', value: '25_34' },
  { id: 4, label: '35-44', value: '35_44' },
  { id: 5, label: '45-54', value: '45_54' },
  { id: 6, label: '55+', value: '55_plus' },
];

// Define book categories
const bookCategories = [
  { id: 1, label: 'Fiction', value: 'fiction' },
  { id: 2, label: 'Non-Fiction', value: 'non_fiction' },
  { id: 3, label: 'Science Fiction', value: 'sci_fi' },
  { id: 4, label: 'Fantasy', value: 'fantasy' },
  { id: 5, label: 'Mystery', value: 'mystery' },
  { id: 6, label: 'Romance', value: 'romance' },
  { id: 7, label: 'Thriller', value: 'thriller' },
  { id: 8, label: 'Biography', value: 'biography' },
  { id: 9, label: 'History', value: 'history' },
  { id: 10, label: 'Self-Help', value: 'self_help' },
  { id: 11, label: 'Business', value: 'business' },
  { id: 12, label: 'Poetry', value: 'poetry' },
  { id: 13, label: 'Young Adult', value: 'young_adult' },
  { id: 14, label: 'Children', value: 'children' },
  { id: 15, label: 'Science', value: 'science' },
  { id: 16, label: 'Technology', value: 'technology' },
  { id: 17, label: 'Health', value: 'health' },
  { id: 18, label: 'Travel', value: 'travel' },
  { id: 19, label: 'Art & Design', value: 'art_design' },
  { id: 20, label: 'Comics & Graphic Novels', value: 'comics' },
];

// Define discovery options
const discoveryOptions = [
  { id: 1, label: 'Social Media', value: 'social_media' },
  { id: 2, label: 'Friend/Family', value: 'friend_family' },
  { id: 3, label: 'App Store/Play Store', value: 'app_store' },
  { id: 4, label: 'Google Search', value: 'google' },
  { id: 5, label: 'Advertisement', value: 'ad' },
  { id: 6, label: 'Other', value: 'other' },
];

const OnboardingScreen: React.FC<OnboardingScreenProps> = ({ navigation }) => {
  // Track the current step of the onboarding process
  const [step, setStep] = useState(1);
  const totalSteps = 4;
  
  // Store the user's selections
  const [readingFrequency, setReadingFrequency] = useState<string | null>(null);
  const [ageGroup, setAgeGroup] = useState<string | null>(null);
  const [selectedCategories, setSelectedCategories] = useState<string[]>([]);
  const [discoverySource, setDiscoverySource] = useState<string | null>(null);
  
  // Handle category selection/deselection
  const toggleCategory = (value: string) => {
    if (selectedCategories.includes(value)) {
      setSelectedCategories(selectedCategories.filter(category => category !== value));
    } else {
      // Limit to 5 categories
      if (selectedCategories.length < 5) {
        setSelectedCategories([...selectedCategories, value]);
      }
    }
  };
  
  // Move to the next step
  const handleNext = () => {
    if (step < totalSteps) {
      setStep(step + 1);
    } else {
      // Submit the onboarding data
      handleFinish();
    }
  };
  
  // Move to the previous step
  const handleBack = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };
  
  // Submit the onboarding data and navigate to the home screen
  const handleFinish = () => {
    // Here you would typically save the user's preferences to your backend
    console.log({
      readingFrequency,
      ageGroup,
      selectedCategories,
      discoverySource
    });
    
    // Navigate to the home screen
    navigation.replace('Home');
  };
  
  // Check if the current step is complete
  const isStepComplete = () => {
    switch (step) {
      case 1:
        return !!readingFrequency;
      case 2:
        return !!ageGroup;
      case 3:
        return selectedCategories.length > 0;
      case 4:
        return !!discoverySource;
      default:
        return false;
    }
  };
  
  // Render the current onboarding step
  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <View style={styles.stepContainer}>
            <Text style={styles.questionTitle}>How often do you read?</Text>
            <Text style={styles.questionSubtitle}>This helps us personalize your experience</Text>
            
            <View style={styles.optionsContainer}>
              {readingFrequencyOptions.map((option) => (
                <TouchableOpacity
                  key={option.id}
                  style={[
                    styles.optionCard,
                    readingFrequency === option.value && styles.selectedOption
                  ]}
                  onPress={() => setReadingFrequency(option.value)}
                >
                  <Text 
                    style={[
                      styles.optionText,
                      readingFrequency === option.value && styles.selectedOptionText
                    ]}
                  >
                    {option.label}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          </View>
        );
        
      case 2:
        return (
          <View style={styles.stepContainer}>
            <Text style={styles.questionTitle}>What is your age group?</Text>
            <Text style={styles.questionSubtitle}>We'll recommend books that resonate with you</Text>
            
            <View style={styles.optionsContainer}>
              {ageGroupOptions.map((option) => (
                <TouchableOpacity
                  key={option.id}
                  style={[
                    styles.optionCard,
                    ageGroup === option.value && styles.selectedOption
                  ]}
                  onPress={() => setAgeGroup(option.value)}
                >
                  <Text 
                    style={[
                      styles.optionText,
                      ageGroup === option.value && styles.selectedOptionText
                    ]}
                  >
                    {option.label}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          </View>
        );
        
      case 3:
        return (
          <View style={styles.stepContainer}>
            <Text style={styles.questionTitle}>What types of books interest you?</Text>
            <Text style={styles.questionSubtitle}>Select up to 5 categories</Text>
            
            <ScrollView style={styles.scrollView}>
              <View style={styles.categoriesContainer}>
                {bookCategories.map((category) => (
                  <Chip
                    key={category.id}
                    selected={selectedCategories.includes(category.value)}
                    onPress={() => toggleCategory(category.value)}
                    style={[
                      styles.categoryChip,
                      selectedCategories.includes(category.value) && styles.selectedCategoryChip
                    ]}
                    selectedColor="#FFFFFF"
                    textStyle={selectedCategories.includes(category.value) ? styles.selectedChipText : {}}
                  >
                    {category.label}
                  </Chip>
                ))}
              </View>
            </ScrollView>
            
            <Text style={styles.selectionCount}>
              {selectedCategories.length} of 5 categories selected
            </Text>
          </View>
        );
        
      case 4:
        return (
          <View style={styles.stepContainer}>
            <Text style={styles.questionTitle}>How did you find Koach Reader?</Text>
            <Text style={styles.questionSubtitle}>We're curious to know</Text>
            
            <View style={styles.optionsContainer}>
              {discoveryOptions.map((option) => (
                <TouchableOpacity
                  key={option.id}
                  style={[
                    styles.optionCard,
                    discoverySource === option.value && styles.selectedOption
                  ]}
                  onPress={() => setDiscoverySource(option.value)}
                >
                  <Text 
                    style={[
                      styles.optionText,
                      discoverySource === option.value && styles.selectedOptionText
                    ]}
                  >
                    {option.label}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          </View>
        );
        
      default:
        return null;
    }
  };
  
  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.header}>
        <Image
          source={require('../../assets/icon.png')}
          style={styles.logo}
        />
        <Text style={styles.headerTitle}>Let's Personalize Your Experience</Text>
      </View>
      
      <View style={styles.progressContainer}>
        <ProgressBar
          progress={step / totalSteps}
          color="#8A2BE2"
          style={styles.progressBar}
        />
        <Text style={styles.progressText}>Step {step} of {totalSteps}</Text>
      </View>
      
      {renderStep()}
      
      <View style={styles.buttonContainer}>
        {step > 1 && (
          <Button
            mode="outlined"
            onPress={handleBack}
            style={styles.backButton}
            labelStyle={styles.backButtonText}
          >
            Back
          </Button>
        )}
        
        <Button
          mode="contained"
          onPress={handleNext}
          disabled={!isStepComplete()}
          style={styles.nextButton}
          contentStyle={styles.nextButtonContent}
          labelStyle={styles.nextButtonText}
        >
          {step === totalSteps ? 'Finish' : 'Next'}
        </Button>
      </View>
      
      <TouchableOpacity 
        style={styles.skipContainer}
        onPress={() => navigation.replace('Home')}
      >
        <Text style={styles.skipText}>Skip for now</Text>
      </TouchableOpacity>
    </SafeAreaView>
  );
};

